CREATE TABLE `messaggio` (
  `id` int(5) NOT NULL,
  `mittente` varchar(50) NOT NULL,
  `destinatario` varchar(50) NOT NULL,
  `testo` varchar(10000) NOT NULL,
  `oggetto` varchar(500) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

ALTER TABLE `messaggio`
  ADD PRIMARY KEY (`id`),
  ADD KEY `MITTENTE` (`mittente`) USING BTREE,
  ADD KEY `DESTINATARIO` (`destinatario`) USING BTREE;

ALTER TABLE `messaggio`
  MODIFY `id` int(5) NOT NULL AUTO_INCREMENT, AUTO_INCREMENT=23;

ALTER TABLE `messaggio`
  ADD CONSTRAINT `messaggio_fk_1` FOREIGN KEY (`mittente`) REFERENCES `utente_loggato` (`email`) ON DELETE CASCADE ON UPDATE CASCADE,
  ADD CONSTRAINT `messaggio_fk_2` FOREIGN KEY (`destinatario`) REFERENCES `utente_loggato` (`email`) ON DELETE CASCADE ON UPDATE CASCADE;



CREATE TABLE `negozio` (
  `email_negozio` varchar(50) NOT NULL,
  `nome` varchar(50) NOT NULL,
  `partitaiva` varchar(50) NOT NULL,
  `indirizzo` varchar(100) NOT NULL,
  `id_carta` int(11) NOT NULL,
  `id_abbonamento` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

ALTER TABLE `negozio`
  ADD PRIMARY KEY (`email_negozio`),
  ADD KEY `CARTA` (`id_carta`),
  ADD KEY `ABBONAMENTO` (`id_abbonamento`);

ALTER TABLE `negozio`
  ADD CONSTRAINT `negozio_fk_1` FOREIGN KEY (`email_negozio`) REFERENCES `utente_loggato` (`email`) ON DELETE CASCADE ON UPDATE CASCADE,
  ADD CONSTRAINT `negozio_fk_2` FOREIGN KEY (`id_abbonamento`) REFERENCES `abbonamento` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  ADD CONSTRAINT `negozio_fk_3` FOREIGN KEY (`id_carta`) REFERENCES `carta` (`id`) ON DELETE CASCADE ON UPDATE CASCADE;


